import React, { PureComponent } from 'react';
import QuestionsSummary from '../components/QuestionsSummary/QuestionsSummary';
import QuestionDetail from '../components/QuestionDetail/QuestionDetail';
import './QuestionsPage.css'

class QuestionsPage extends PureComponent{

    constructor(props) {
        super(props);
        this.state = {
			questions: [],
		  	selectedQuestionItem: {},
			viewQuestion: false,
			newQuestion: '',
			newAnswer: ''
        };
      }

	onBackButtonClicked = () =>{
		this.setState({selectedQuestionItem: {} });
		this.setState({ viewQuestion: false});
	};

	onQuestionItemSelected = (id)=>{
		const questionItemSelected = this.state.questions.filter( question => question.id === id);
		this.setState({selectedQuestionItem: questionItemSelected[0]});
		this.setState({ viewQuestion: true});
	};


    obtainQuestionsSummaryList = (questions) => {
        if(questions !== undefined){
            const questionsList = [...questions];
            const questionSummary = questionsList.map( question => {
                return { questionId: question.id , question: question.text, answers: question.answers.length }
            }) || [];


            return questionSummary;
        }
        return [];
    };

	fetchQuestions = async () => {

	};

	saveQuestion = async (newQuestion, username) => {

	}

	saveAnswer = async (newAnswer, username, questionId) => {

	}

	onNewQuestionChange = (event) =>{
		this.setState({newQuestion: event.target.value});
	}

	onNewQuestionSubmit = (event, username) => {
		if (window.confirm('Tem certeza que deseja enviar pergunta?')){
			this.saveQuestion(this.state.newQuestion, username)
		}
		event.preventDefault();

	};

	onNewAnswerChange = (event) =>{
		this.setState({newAnswer: event.target.value});
	}

	onNewAnswerSubmit = (event, username, questionId) => {
		if (window.confirm('Tem certeza que deseja enviar resposta?')){
			this.saveAnswer(this.state.newAnswer, username, questionId)
		}
		event.preventDefault();

	};

    componentDidMount = () => {
        const newQuestions = [...questionsConst];
        this.setState({ questions : newQuestions})
    }

    render(){
        const { questions, viewQuestion , selectedQuestionItem} = this.state;
        return(
           <div className='QuestionsPage'>
			   {viewQuestion? 
			   <QuestionDetail 
					backButtonClicked={this.onBackButtonClicked}
					selectedQuestion={selectedQuestionItem}
					newAnswer={this.state.newAnswer}
					onNewAnswerChange={this.onNewAnswerChange}
					onNewAnswerSubmit={this.onNewAnswerSubmit} /> 
			   :  
			   <QuestionsSummary
					onQuestionSelected={this.onQuestionItemSelected}
					newQuestion={this.state.newQuestion} 
					onNewQuestionChange={this.onNewQuestionChange}
					onNewQuestionSubmit = {this.onNewQuestionSubmit}   
                	questionsSummaryList={this.obtainQuestionsSummaryList(questions)} /> }
           </div>
        );

    };
}

export default QuestionsPage;



