import React from 'react';
import AuthContext from '../../../context/AuthContext';
import './Forms.css';

const addAnswerForm = props => {

    return(
        <form onSubmit={(event) => props.onNewAnswerSubmit(event, props.username, props.questionId)}>
            <p>Quer adicionar uma resposta?</p>
            <textarea 
                value={props.newAnswer}
                onChange={event => props.onNewAnswerChange(event)}
                className="FormsTextArea"
                placeholder="Sua resposta"   
            />
            <input type="submit" value="Enviar" />
        </form>
    );
}

export default addAnswerForm;
