import React, { PureComponent } from 'react';
import QuestionsSummary from '../components/QuestionsSummary/QuestionsSummary';
import QuestionDetailPage from './QuestionDetailPage';
import './QuestionsPage.css'

import { listQuestions, createQuestion } from '../services/Question';
import { findAnswersByQuestionId } from '../services/Answer';


class QuestionsPage extends PureComponent{

    constructor(props) {
        super(props);
        this.state = {
			questions: [],
		  	selectedQuestionItem: {},
			viewQuestionDetail: false,
			newQuestion: ''
        };
    }

	fetchQuestions = async () => {
		const questions = await listQuestions();
		this.setState({ questions: [...questions]});	
	};

	saveQuestion = async (newQuestion, username) => {
		const new_question = { text: newQuestion, user: username }
		await createQuestion(new_question);
	}

	fetchAnswersNumberByQuestion = async () =>{
		const questions = [...this.state.questions];
		let new_questions = [];
		for(const question of questions) {
			const fetch_answers = await findAnswersByquestionId(question.id);
			const new_question = { ...question, answers: [...fetch_answers]};
			new_questions.push(new_question);
		};
		this.setState({ questions: [...new_questions]});	
	};


	onBackButtonClicked = () =>{
		this.setState({ viewQuestionDetail: false});
	};

	onQuestionItemSelected = (id)=>{
		const questionItemSelected = this.state.questions.filter( question => question.id === id);
		this.setState({selectedQuestionItem: questionItemSelected[0]});
		this.setState({ viewQuestionDetail: true});
	};

	onNewQuestionChange = (event) =>{
		this.setState({newQuestion: event.target.value});
	}

	onNewQuestionSubmit = async (event, username) => {
		event.preventDefault();
		if (window.confirm('Tem certeza que deseja enviar pergunta?')){
			await this.saveQuestion(this.state.newQuestion, username);
			await this.fetchQuestions();
			this.setState({ newQuestion: ''});
		}
		this.fetchAnswersNumberByQuestion();
	};

	componentDidUpdate = async(prevProps, prevState) =>{
		if(prevState.viewQuestionDetail !== this.state.viewQuestionDetail){
			await this.fetchQuestions();
			this.fetchAnswersNumberByQuestion();
		}
	};

    componentDidMount = async() => {
		await this.fetchQuestions();
		this.fetchAnswersNumberByQuestion();
    }

    render(){
		const { questions, newQuestion, viewQuestionDetail , selectedQuestionItem} = this.state;
        return(
           <div className='QuestionsPage'>
			   {viewQuestionDetail? 
			   <QuestionDetailPage 
					backButtonClicked={this.onBackButtonClicked}
					selectedQuestion={selectedQuestionItem} /> 
			   :  
			   <QuestionsSummary
					onQuestionSelected={this.onQuestionItemSelected}
					newQuestion={newQuestion} 
					onNewQuestionChange={this.onNewQuestionChange}
					onNewQuestionSubmit = {this.onNewQuestionSubmit}   
                	questionsSummaryList={questions} /> }
           </div>
        );  
    };
}

export default QuestionsPage;
