import axios from 'axios';
import config from './config';

export const findAnswersByQuestionId = async (questionId) =>{
    const answers = await axios.get(`/answer/find/${questionId}`, config);
    if(answers.status === 204){
        return [];
    }
    return answers.data;
};

export const createAnswer =  async(answer) => {
   return await axios.post('/answer/save', answer, config);
};
