import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import BackButton from '../components/UI/BackButton/BackButton';
import AnswerList from '../components/QuestionDetail/AnswerList/AnswerList';
import QuestionBox from '../components/QuestionDetail/QuestionBox/QuestionBox';
import AddAnswerForm from '../components/UI/Forms/AddAnswerForm';
import AuthContext from '../context/AuthContext';
import './QuestionDetailPage.css';


class QuestionDetailPage extends PureComponent{

    render(){
        const selectedQuestion = {...this.props.selectedQuestion};

        return(
            <div className='QuestionDetailPage'>
                <BackButton clicked={() => this.props.backButtonClicked()} />
                <QuestionBox 
                    question={selectedQuestion.text}
                    user={selectedQuestion.user}
                    answers={selectedQuestion.answers.length || 0} />
                <AnswerList answerList = {selectedQuestion.answers}/>

                <AuthContext.Consumer>
                    {context => <AddAnswerForm 
                        questionId = {selectedQuestion.id}
                        username={context.username}
                        newAnswer={this.props.newAnswer}
                        onNewAnswerChange={this.props.onNewAnswerChange}
                        onNewAnswerSubmit={this.props.onNewAnswerSubmit}/>}
                </AuthContext.Consumer>
            </div>
        );
    }
}

QuestionDetailPage.defaultProps = {
    selectedQuestion: {
        id: 0,
        text: '',
        user: '',
        creationDate: '',
        answers:[]
    }
};

QuestionDetailPage.propTypes = {
    backButtonClicked : PropTypes.func,
    selectedQuestion: PropTypes.object,
    newAnswer: PropTypes.string,
    onNewAnswerChange: PropTypes.func,
    onNewAnswerSubmit: PropTypes.func
};

export default QuestionDetailPage;
