import React, { PureComponent } from 'react';
import QuestionsSummary from '../components/QuestionsSummary/QuestionsSummary';
import BackButton from '../components/UI/BackButton/BackButton';
import './QuestionsPage.css'

class QuestionsPage extends PureComponent{

    constructor(props) {
        super(props);
        this.state = {
          questions: [],
		  selectedQuestionItem: {},
		  viewQuestion: true
        };
      }


	onQuestionItemSelected = (id)=>{
		const questionItemSelected = this.state.questions.filter( question => question.id === id);

	};


    obtainQuestionsSummaryList = (questions) => {
        if(questions !== undefined){
            const questionsList = [...questions];
            const questionSummary = questionsList.map( (question, key) => {
                return { questionId: question.id , question: question.text, answers: question.answers.length }
            }) || [];


            return questionSummary;
        }
        return [];
    };

    componentDidMount = () => {
        const newQuestions = [...questionsConst];
        this.setState({ questions : newQuestions})
    }

    render(){
        const { questions, viewQuestion } = this.state;
        return(
           <div className='QuestionsPage'>
			   {viewQuestion? <BackButton /> : null }

               <QuestionsSummary 
                	questionsSummaryList={this.obtainQuestionsSummaryList(questions)} />
           </div>
        );

    };
}

export default QuestionsPage;


