import React from 'react';
import PropTypes from 'prop-types';
import './Forms.css';

const questionForm = props => {

    return(
        <form onSubmit={(event) => props.onNewQuestionSubmit(event, props.username)}>
            <p>Quer fazer um pergunta?</p>
            <textarea 
                value={props.newQuestion}
                onChange={event => props.onNewQuestionChange(event)}
                className="FormsTextArea"
                placeholder="Sua pergunta"   
            />
            <input type="submit" value="Enviar" />
        </form>
    );
}

questionForm.propTypes = {
    newQuestion: PropTypes.string,
    onNewQuestionChange: PropTypes.func,
    onNewQuestionSubmit: PropTypes.func,
    username: PropTypes.string
};

export default questionForm;
