import axios from 'axios';
import config from './config';

export const listQuestions = async () =>{
    try {
        const questions = await axios.get('/question/all', config);
        if(questions.status === 204){
            return { questions :[], error: false};
        }
        return { questions: questions.data, error: false} ;
    } catch (error) {
        return { questions :[], error: true, message: error};
    }
};

export const createQuestion =  async(question) => {
    try {
        const data = await axios.post('/question/save', question, config);
        return { question: data, error: false }
    } catch (error) {
        return { question : {}, error: true, message: error};
    }

};
