import axiosInstance from './config';

export const findAnswersByQuestionId = async (questionId) =>{
    const answers = await axiosInstance.get(`/answer/find/${questionId}`);
    if(answers.status === 204){
        return [];
    }
    return answers.data;
};

export const createAnswer =  async(answer) => {
   return await axiosInstance.post('/answer/save', answer);
};
