import React from 'react';
import BackButton from '../UI/BackButton/BackButton';
import AnswerList from '../QuestionDetail/AnswerList/AnswerList';
import QuestionBox from './QuestionBox/QuestionBox';
import AddAnswerForm from '../UI/Forms/AddAnswerForm';
import './QuestionDetail.css';

const questionDetail = props =>{
    const selectedQuestion = {...props.selectedQuestion};

    return(
        <div className='QuestionDetail'>
            <BackButton clicked={() => props.backButtonClicked()} />
            <QuestionBox 
                question={selectedQuestion.text}
                user={selectedQuestion.user}
                answers={selectedQuestion.answers.length || 0} />
            <AnswerList answerList = {selectedQuestion.answers}/>
            <AddAnswerForm />
        </div>
    );
};

export default questionDetail;
