import axios from 'axios';
import config from './config';

export const listQuestions = async () =>{
    try {
        const response = await axios.get('/question/all', config);
        console.log(response)
        if(response.status === 200){
            return { questions: response.data, error: false} ;
        }
        if(response.status === 204){
            return { questions :[], error: false};
        }
        if(response.status === 400){
            return { error: true, message: "Recurso não existe."};
        }
        if(response.status === 500){
            return { error: true, message: "Erro interno do servidor."}
        }
        else{
            return { error: true, message: "Erro desconhecido."}
        }
    } catch (error) {
        return { error: true, message: error};
    }
};

export const createQuestion =  async(question) => {
    try {
        const response = await axios.post('/question/save', question, config);
        return { question: response.data, error: false }
    } catch (error) {
        return { question : {}, error: true, message: error};
    }

};
