import React from 'react';
import QuestionSummaryItem from './QuestionSummaryItem';
import QuestionArea from './QuestionArea';

import './QuestionsSummary.css';

const questionsSummary = props => {
    const questionSummaryItems = props.questionsSummaryList.map((questionSummary, index) => {
        return <QuestionSummaryItem 
                selectQuestion = {props.selectQuestion}
                question = {questionSummary.question}
                answers = {questionSummary.answers}
                id = {questionSummary.questionId}
                key={questionSummary.questionId}/>;
    });

    return (
        <div className="questionSummary">
            <p>Perguntas</p> 
            {questionSummaryItems}
            <QuestionArea 
                newQuestion={props.newQuestion}
                onNewQuestionChange={props.onNewQuestionChange}
                createQuestion={props.createQuestion} />
        </div>
    );
};

export default questionsSummary;
